import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getOfficerById } from '../api/api';
import { LoginPageProps } from '../types/api';

const LoginPage: React.FC<LoginPageProps> = ({ setOfficerId }) => {
  const [inputOfficerId, setInputOfficerId] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>('');
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!inputOfficerId.trim()) {
      setError('Please enter an Officer ID');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Validate officer exists
      await getOfficerById(inputOfficerId);

      // Set officer ID and navigate
      setOfficerId(inputOfficerId);
      navigate('/select-subject');
    } catch (err: any) {
      if (err.response?.status === 404) {
        setError('Officer not found. Please check your Officer ID.');
      } else {
        setError('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: '400px', margin: '100px auto' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '24px' }}>Login</h2>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="officerId">Officer ID</label>
            <input
              type="number"
              id="officerId"
              value={inputOfficerId}
              onChange={(e) => setInputOfficerId(e.target.value)}
              placeholder="Enter your Officer ID"
              disabled={loading}
              required
            />
          </div>

          <button 
            type="submit" 
            className="btn"
            style={{ width: '100%' }}
            disabled={loading}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <div style={{ textAlign: 'center', marginTop: '20px', color: '#666' }}>
          <p>Enter your Officer ID to access the contest system</p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
