import React, { useState, useEffect } from 'react';
import { getOfficers } from '../api/api';
import { Officer } from '../types/api';

type SortBy = 'score' | 'name' | 'unit' | 'tests';
type SortOrder = 'asc' | 'desc';

const LeaderBoardPage: React.FC = () => {
  const [officers, setOfficers] = useState<Officer[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');
  const [sortBy, setSortBy] = useState<SortBy>('score');
  const [sortOrder, setSortOrder] = useState<SortOrder>('desc');

  useEffect(() => {
    const fetchOfficers = async (): Promise<void> => {
      try {
        setLoading(true);
        const officersData = await getOfficers();
        setOfficers(officersData);
      } catch (err) {
        setError('KhÃ´ng thá»ƒ táº£i báº£ng xáº¿p háº¡ng. Vui lÃ²ng thá»­ láº¡i.');
        console.error('Error fetching officers:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchOfficers();
  }, []);

  const sortedOfficers = React.useMemo(() => {
    if (!officers) return [];

    return [...officers].sort((a, b) => {
      let aValue: string | number, bValue: string | number;

      switch (sortBy) {
        case 'score':
          aValue = a.score || 0;
          bValue = b.score || 0;
          break;
        case 'name':
          aValue = a.name || '';
          bValue = b.name || '';
          break;
        case 'unit':
          aValue = a.unit?.name || '';
          bValue = b.unit?.name || '';
          break;
        case 'tests':
          aValue = a.list_submission?.length || 0;
          bValue = b.list_submission?.length || 0;
          break;
        default:
          aValue = a.score || 0;
          bValue = b.score || 0;
      }

      if (typeof aValue === 'string') {
        const comparison = aValue.localeCompare(bValue as string);
        return sortOrder === 'asc' ? comparison : -comparison;
      }

      return sortOrder === 'asc' ? (aValue as number) - (bValue as number) : (bValue as number) - (aValue as number);
    });
  }, [officers, sortBy, sortOrder]);

  const handleSort = (column: SortBy): void => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder(column === 'score' || column === 'tests' ? 'desc' : 'asc');
    }
  };

  const getRankIcon = (index: number): string => {
    switch (index) {
      case 0: return 'ðŸ¥‡';
      case 1: return 'ðŸ¥ˆ';
      case 2: return 'ðŸ¥‰';
      default: return `#${index + 1}`;
    }
  };

  const getScoreColor = (score: number): string => {
    if (score >= 80) return '#28a745';
    if (score >= 60) return '#ffc107';
    return '#dc3545';
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading leaderboard...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <h2>Leaderboard</h2>
        <p>Ranking of all officers based on their test performance</p>

        {/* Statistics */}
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
          gap: '16px', 
          margin: '20px 0',
          padding: '20px',
          background: '#f8f9fa',
          borderRadius: '8px'
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#007bff' }}>
              {officers.length}
            </div>
            <div style={{ color: '#666' }}>Total Officers</div>
          </div>

          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#28a745' }}>
              {officers.filter(o => (o.score || 0) >= 80).length}
            </div>
            <div style={{ color: '#666' }}>High Scorers (â‰¥80%)</div>
          </div>

          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#ffc107' }}>
              {officers.reduce((sum, o) => sum + (o.list_submission?.length || 0), 0)}
            </div>
            <div style={{ color: '#666' }}>Total Tests Taken</div>
          </div>

          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#17a2b8' }}>
              {officers.length > 0 ? 
                Math.round(officers.reduce((sum, o) => sum + (o.score || 0), 0) / officers.length) 
                : 0}%
            </div>
            <div style={{ color: '#666' }}>Average Score</div>
          </div>
        </div>
      </div>

      <div className="card">
        {officers.length === 0 ? (
          <p>No officers found.</p>
        ) : (
          <div style={{ overflowX: 'auto' }}>
            <table className="leaderboard-table">
              <thead>
                <tr>
                  <th>Rank</th>
                  <th 
                    onClick={() => handleSort('name')}
                    style={{ cursor: 'pointer', userSelect: 'none' }}
                  >
                    Name {sortBy === 'name' && (sortOrder === 'asc' ? 'â†‘' : 'â†“')}
                  </th>
                  <th>Rank</th>
                  <th>Position</th>
                  <th 
                    onClick={() => handleSort('unit')}
                    style={{ cursor: 'pointer', userSelect: 'none' }}
                  >
                    Unit {sortBy === 'unit' && (sortOrder === 'asc' ? 'â†‘' : 'â†“')}
                  </th>
                  <th 
                    onClick={() => handleSort('score')}
                    style={{ cursor: 'pointer', userSelect: 'none' }}
                  >
                    Score {sortBy === 'score' && (sortOrder === 'asc' ? 'â†‘' : 'â†“')}
                  </th>
                  <th 
                    onClick={() => handleSort('tests')}
                    style={{ cursor: 'pointer', userSelect: 'none' }}
                  >
                    Tests Taken {sortBy === 'tests' && (sortOrder === 'asc' ? 'â†‘' : 'â†“')}
                  </th>
                </tr>
              </thead>
              <tbody>
                {sortedOfficers.map((officer, index) => (
                  <tr key={officer.id}>
